defmodule Infuse.HTTP.Pipeline do
  @moduledoc """
  The main HTTP pipeline for serving requests. 

  The firing order is:
   * Static Requests
   * Simplate Routes
   * Not Found
  """

  use Plug.Builder

  if Mix.env == :dev do
    use Plug.Debugger, otp_app: :infuse, style: [primary: "#00A359", logo: "data:image/png;base64,"]
  end

  plug Plug.Logger, log: :debug

  plug Plug.Static,
    at: "/",
    from: Infuse.config(:web_root)

  plug Plug.Parsers, parsers: [:urlencoded]

  plug Infuse.HTTP.SimplateRouter

  plug :not_found

  def not_found(conn, _) do
    conn
    |> send_resp(404, "Could not find: " <> Infuse.config(:web_root) <> conn.request_path)
    |> halt
  end

end
